mapper.select:
SELECT COALESCE(SUM(amount), 0)
  FROM folio.account_transaction
 WHERE account_id = #{accountId}
   AND type = #{type}
   AND transacted_at >= CURRENT_DATE
   AND transacted_at < CURRENT_DATE + INTERVAL '1 day'

SELECT COALESCE(SUM(amount), 0)
  FROM folio.account_transaction
 WHERE account_id = #{accountId}
   AND type = #{type}

SELECT id, account_id, type, amount, memo, transacted_at, created_at
  FROM folio.account_transaction
 WHERE id = #{id}

mapper.update:
UPDATE folio.account_transaction
   SET type = #{type},
       amount = #{amount},
       memo = #{memo}
 WHERE id = #{id}
